function makeId(length) {
    let result = '';
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const charactersLength = characters.length;
    let counter = 0;
    while (counter < length) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength));
      counter += 1;
    }
    return result;
}

const userData = (name = 'sky net', dateBirth = '01.01.1970', purpose = 'destroy world') => {
  const id = makeId(10);
  const firstName = name.split(' ')[0][0].toUpperCase() + name.split(' ')[0].slice(1);
  const secondName = name.split(' ')[1][0].toUpperCase() + name.split(' ')[1].slice(1);
  const age = Math.floor((new Date().getTime() - new Date(dateBirth).getTime()) / (1000 * 60 * 60 * 24 * 365));
  purpose = purpose[0].toUpperCase() + purpose.slice(1);

  return { id, firstName, secondName, dateBirth, age, purpose };
}

export default userData;
